import { Component } from 'react/cjs/react.production.min';
import './charInfo.scss';
import thor from '../../resources/img/thor.jpeg';

class CharInfo extends Component {

    state = {
        char: {},
        loading: false,
        error: false
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.updateChar();
    }

    updateChar = () => {
        const {charId} = this.props;
        if(!charId) {
            return;
        }

        this.onCharLoading;

        this.marvelService
            .getCharacter(charId)
            .then(this.onCharLoaded)
            .catch(this.this.onError);
    }

    onCharLoaded = (char) => {
        this.setState({
            char, 
            loading: false
        })
    }

    onCharLoading = () => {
        this.setState({
            loading: true
        })
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true
        })
    }
    
    render() {
        return (
            <div className="char__info">
                
            </div>
        )
    }
}

const View = ({char}) => {
    return (
        <>
        </>
    )
}

export default CharInfo;
